import React from 'react'
import { Modal, StyleSheet, Text, TouchableOpacity, View } from 'react-native'
import Share, {ShareSheet, Button} from 'react-native-share'
import Icon from 'react-native-vector-icons/MaterialIcons'

import { colors } from '../../../theme'

// Example From
// https://github.com/EstebanFuentealba/react-native-share

const ShareModal = ({ cancelShare, visible }) => {
  const onCancel = () => console.log('Cancel clicked on social share')
  const shareOptions = {
    title: 'Share Title',
    message: 'Share Message',
    url: 'http://facebook.github.io/react-native/',
    subject: 'Subject Title' //  for email
  }

  return (
    <Modal visible={visible} onRequestClose={onCancel} transparent animationType={'fade'}>
      <View style={styles.container}>
        <View style={styles.modalContainer}>
          <View style={styles.topContainer}>
            <TouchableOpacity onPress={cancelShare}>
              <Icon name="close" color={colors.gray70} size={25} style={styles.close} />
            </TouchableOpacity>
          </View>
          <Button
            iconSrc={{ uri: TWITTER_ICON }}
            onPress={()=>{
            onCancel()
            setTimeout(() => {
              Share.shareSingle(Object.assign(shareOptions, {
                'social': 'twitter'
              }))
            },300)
          }}>Twitter</Button>
          <Button iconSrc={{ uri: FACEBOOK_ICON }}
                  onPress={()=>{
              onCancel()
              setTimeout(() => {
                Share.shareSingle(Object.assign(shareOptions, {
                  'social': 'facebook'
                }))
              },300)
            }}>Facebook</Button>
            <Button iconSrc={{ uri: WHATSAPP_ICON }}
                  onPress={()=>{
              onCancel();
              setTimeout(() => {
                Share.shareSingle(Object.assign(shareOptions, {
                  "social": "whatsapp"
                }));
              },300);
            }}>Whatsapp</Button>
          <Button iconSrc={{ uri: GOOGLE_PLUS_ICON }}
                  onPress={()=>{
              onCancel();
              setTimeout(() => {
                Share.shareSingle(Object.assign(shareOptions, {
                  "social": "googleplus"
                }));
              },300);
            }}>Google +</Button>
          <Button iconSrc={{ uri: EMAIL_ICON }}
                  onPress={()=>{
              onCancel();
              setTimeout(() => {
                Share.shareSingle(Object.assign(shareOptions, {
                  "social": "email"
                }));
              },300);
            }}>Email</Button>
          </View>
        </View>
    </Modal>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'rgba(0,0,0,0.5)',
    justifyContent: 'center',
    alignItems: 'center',
  },
  modalContainer: {
    paddingBottom: 20,
    width: 320,
    backgroundColor: colors.white,
    elevation: 4,
    justifyContent: 'center',
    // alignItems: 'center',
  },
  topContainer: {
    height: 50,
    paddingHorizontal: 20,
    justifyContent: 'center',
    alignItems: 'flex-end',
  },
})

//  twitter icon
const TWITTER_ICON = 'data:image/png;base64,'

//  facebook icon
const FACEBOOK_ICON = 'data:image/png;base64,'

//  whatsapp icon
const WHATSAPP_ICON = 'data:image/png;base64,'

//  gplus icon
const GOOGLE_PLUS_ICON = 'data:image/png;base64,'

//  email icon
const EMAIL_ICON = 'data:image/png;base64,'

export default ShareModal
